import React from "react";
import { Canvas } from "@react-three/fiber";
import { OrbitControls, Stars, stars, useTexture } from "@react-three/drei";

import mars from '../src/textures/2k_earth_daymap.jpg'
import earth from '../src/textures'

function Planet({ textureUrl, position = [0, 0, 0], radius = 1 }) {
  const texture = useTexture(textureUrl);
  return (
    <mesh position={position}>
      <sphereGeometry args = {[radius, 64, 64]} />
      <meshStandardMaterial map={texture} />
    </mesh>
  );
}


export default function App() {
  return (
    <Canvas camera={{ position : [0, 0, 5] }}>
      <ambientLight intensity={0.5} />
      <pointLight position={[10, 10, 10]} />
      <Stars />
      <Planet textureUrl="src/textures/2k_earth_daymap.jpg" position={[0, 0, 0]} radius={1.5} />
      <Planet textureUrl="src/textures/2k_mars.jpg" position={[3, 0, 0]} radius={2} />
      <OrbitControls />
    </Canvas>
  )
}
