import React from "react";
import { useState, useRef } from "react";
import { Canvas, useLoader, useFrame } from "@react-three/fiber";
import { OrbitControls, Stars, stars} from "@react-three/drei";
import { TextureLoader } from "three";
import styled from "styled-components";

import sun from '../src/textures/2k_sun.jpg'
import mars from '../src/textures/2k_mars.jpg'

// a -> semi major axis
// e -> eccentricity
// I -> inclination
// L -> mean longitue
// beta -> longitude of perihelion
// omega -> longitude of the ascending node

class OrbitalElements {
  constructor(a, e, I, L, omegaBar, omega, jd, jdEPOCH) {
    this.a = a;
    this.e = e;
    this.I = I * Math.PI / 180;  // inclination (radians)
    this.L = L * Math.PI / 180;  // mean longitude (radians)
    this.omegaBar = omegaBar * Math.PI / 180; // longitude or perihelion
    this.omega = omega * Math.PI / 180; //longitude of ascending node (you guessed it)
    this.jd = jd;
    this.jdEPOCH = 2451545.0; // epoch julian date J2000
  }

  // between 0 and 2pi
  normalizeAngle(angle) { 
    angle = angle % (2 * Math.PI);
    return angle < 0 ? angle + 2 * Math.PI : angle;
  }

  // at given julian date
  meanAnomaly(JD) {
    // M = M0 + n * (JD - JD0);
    const n = Math.sqrt( 1 / (this.a ** 3));
    let M0 = this.normalizeAngle(this.L - this.omegaBar);
    let M = M0 + n * (JD - this.jdEPOCH);
    return this.normalizeAngle(M);
  }

  solveKepler(M) {
    // M = E - e sin E (solve for E)

    // using newton-raphson iteration
    let E = M;
    let delta;

    do {
      delta = (E - this.e * Math.sin(E) - M) / 
      (1 - this.e * Math.cos(E));
      E -= delta;

    } while (Math.abs(delta) > 1e-6);
  }

  to3Dspace(JD) {

    const M = this.meanAnomaly(JD);
    const E = this.solveKepler(M);

    // orbital plane 
    const xPrime = this.a * (Math.cos(E) - this.e);
    const yPrime = this.a * Math.sqrt( 1 - e ** 2) * Math.sin(E);

    // arguement of parihelion
    const omegaPar = this.omegaBar - this.omega;

    // rotate to 3D space 
    const cosOmega = Math.cos(this.omega);
    const sinOmega = Math.sin(this.omega);
    const cosOmegaPar = Math.cos(omegaPar);
    const sinOmegaPar = Math.sin(omegaPar);
    const cosI = Math.cos(this.I);
    const sinI = Math.sin(this.I);

    const x = (cosOmega * cosOmegaPar - sinOmega * sinOmegaPar * cosi) * xPrime +
              (-cosOmega * sinOmegaPar - sinOmega * cosOmegaPar * cosi) 


  }
}

function toJulianDate(date = new Date()) {
  const time = date.getTime();
  return time / 86400000 + 2440587.5 // ms in day + Unix epoch (Jan 1, 1970)
}

function fromJulianDate(jd) {
  return new Date((jd - 2440587.5) * 86400000);
}

function Planet({ textureFile, position = [0, 0, 0], radius}) {
  const texture = useLoader(TextureLoader, textureFile);
  return (
    <mesh position={position}>
      <sphereGeometry args = {[radius, 64, 64]} />
      <meshStandardMaterial map={texture} />
    </mesh>
  );
}

function SimulateTime({ onUpdate, paused }) {
  const [simdT, setSimdT] = useState(toJulianDate());
  const speed = 10;

  useFrame((_, delta) => {
    if (paused) return;
    const newSimdT = simdT + delta * speed;
    setSimdT(newSimdT);
    onUpdate(newSimdT);
  })
  return null;
}

export default function App() {
  const [gregDate, setGregDate] = useState(new Date());
  const [paused, setPaused] = useState(false);

  return (
    <>
      <Canvas camera={{ position : [0, 0, 10] }}>
        <ambientLight intensity={0.5} />
        <pointLight position={[10, 10, 10]} />
        <Stars />
        <SimulateTime 
          paused={paused}
          onUpdate={(jd) => {setGregDate(fromJulianDate(jd))}} 
        />
        <Planet textureFile={sun} position={[0, 0, 0]} radius={2} />
        <Planet textureFile={mars} position={[5, 0, 0]} radius={1} />
        <OrbitControls />
      </Canvas>

      <DateDisplay>
        {gregDate.toDateString()} <br />
        {gregDate.toLocaleDateString()}
      </DateDisplay>
      <Controls>
        <button onClick={() => setPaused(true)}>pause</button>
      </Controls>
    </>
  )
}


const DateDisplay = styled.div`
  position: absolute;
  top: 20px;
  left: 20px;
  color: black;
  font-family: monospace;
  background: white;
`

const Controls = styled.div`
  position: absolute;
  top: 60px;
  left: 20px
`
