import React from "react";
import { Canvas, useLoader } from "@react-three/fiber";
import { OrbitControls, Stars, stars, useTexture } from "@react-three/drei";
import { TextureLoader } from "three";

import sun from '../src/textures/2k_sun.jpg'
import mars from '../src/textures/2k_mars.jpg'

function toJulianDate(date = new Date()) {
  const time = date.getTime();
  return time / 86400000 + 2440587.5 // ms in day + Unix epoch (Jan 1, 1970)
}

function Planet({ textureFile, position = [0, 0, 0], radius}) {
  const texture = useLoader(TextureLoader, textureFile);
  return (
    <mesh position={position}>
      <sphereGeometry args = {[radius, 64, 64]} />
      <meshStandardMaterial map={texture} />
    </mesh>
  );
}

      console.log(toJulianDate(new Date("2025-06-2011")));

export default function App() {
  return (
    <Canvas camera={{ position : [0, 0, 10] }}>
      <ambientLight intensity={0.5} />
      <pointLight position={[10, 10, 10]} />
      <Stars />
      <Planet textureFile={sun} position={[0, 0, 0]} radius={2} />
      <Planet textureFile={mars} position={[5, 0, 0]} radius={1} />
      <OrbitControls />
    </Canvas>
  )
}
